import React from 'react'
import { useDispatch } from 'react-redux'
import { createAnecdote } from '../reducers/anecdoteReducer'
import { notificationChange, notificationRemove, setNotification } from '../reducers/notificationReducer'


const AnecdoteForm = () => {
    const dispatch = useDispatch()
    const add = async (event) => {
        event.preventDefault()
        const content = event.target.anecdote.value
        event.target.anecdote.value = ''
        dispatch(createAnecdote(content))
        // dispatch(notificationChange('You successfully created an anecdote!'))
        // setTimeout(() => dispatch(notificationRemove()), 5000)
        dispatch(setNotification(`you successfully created the anecdote: ${content}`, 5))
    }
    return (
        <div>
        <h2>create new</h2>
        <form onSubmit={add}>
          <div><input name="anecdote" /></div>
          <button type="submit">create</button>
        </form>
        </div>
    )
}

export default AnecdoteForm
