import React from 'react'
import { useDispatch } from 'react-redux'
import { createAnecdote } from '../reducers/anecdoteReducer'
import { notificationChange, showNotification, removeNotification } from '../reducers/notificationReducer'



const AnecdoteForm = () => {
    const dispatch = useDispatch()
    const add = (event) => {
        event.preventDefault()
        dispatch(createAnecdote(event.target.anecdote.value))
        // dispatch(notificationChange('You successfully created an anecdote!'))
        dispatch(showNotification('You successfully created an anecdote!'))
        dispatch(removeNotification())


    }
    return (
        <div>
        <h2>create new</h2>
        <form onSubmit={add}>
          <div><input name="anecdote" /></div>
          <button type="submit">create</button>
        </form>
        </div>
    )
}

export default AnecdoteForm
